import React from "react";
import { useEffect, useState } from "react";
import { vendiaClient } from "./vendiaClient";

const { client } = vendiaClient();

export const Demo = () => {

    const [device, setDevice] = useState();
    const [testID, setTestID] = useState(0); // Initialize as 0 or any default integer value
    const [orgAssignment, setOrgAssignment] = useState("");
    const [testName, setTestName] = useState() ;
    const [testMethod, setTestMethod] = useState("");
    const [notes, setNotes] = useState("");
    const [completed, setCompleted] = useState(True); // Initialize as True or any default boolean value
    //const [testList, setTestList] = useState([]) ;



    useEffect(() => {
        const listTest = async () => {
            const listTestResponse = await client.entities.test.list();
            console.log(listTestResponse?.items);
            setTestList(listTestResponse?.items);
        }

    //listTest();
    }, [])

    const addDevice = async () => {
        const addDeviceResponse = await client.entities.test.add ({
            Device: device, 
            TestID: testID,
            OrgAssignment:orgAssignment,
            TestName: testName,
            TestMethod:testMethod,
            Notes:notes,
            Completed: completed,
        })
        console.log(addDeviceResponse);
    }

    const handleDeviceChange = (event) => {
        setDevice(event.target.value);
        //console.log(device);
    }

    const handleTestIDChange = (event) => {
        setTestID(event.target.value);
        //console.log(device);
    }

    const handleOrgAssignmentChange = (event) => {
        setOrgAssignment(event.target.value);
        //console.log(device);
    }

    const handleTestNameChange = (event) => {
        setTestName(event.target.value);
        //console.log(testName);
    }

    const handleTestMethodChange = (event) => {
        setTestMethod(event.target.value);
        //console.log(device);
    }
    const handleNotesChange = (event) => {
        setNotes(event.target.value);
        //console.log(device);
    }

    const handleCompletedChange = (event) => {
        setCompleted(event.target.value);
        //console.log(device);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        addDevice();
    }

    console.log(testList)

    return (
        <div>
            Team Zephyr Device Test Tracker
            <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <input 
                    type="text"
                    name="Device"
                    value={device}
                    onChange={handleDeviceChange}
                    />
                </div>
                <div>
                    <input 
                    type="text"
                    name="testName"
                    value={testName}
                    onChange={handleTestNameChange}
                    />
                </div>
                <input type="Submit" />
            </form>
            <div>
                {testList?.map((item, index) => (
                    <div key={index}>
                        {item?.TestID}
                    </div>
                ))
                }
            </div>
            </div>
        </div>
    )
};
